package Week1.Day1;

public class Factorial {
	public static void main(String[]args) {
		// TODO Auto-generated method stub
		int Num = 5;
		int fact = 1;
		for (int i=1; i <= Num; i++) {
			fact=fact*i;
		
	}
		System.out.println("Factorial of Number " + Num +" is: " + fact); 

	}
}
